const raw = ($json.text || $json.response || '').trim();
let result;

try {
  result = JSON.parse(raw);
} catch (e) {
  const m = raw.match(/```(?:json)?\s*([\s\S]*?)```/);
  const candidate = m ? m[1].trim() : raw;
  try {
    const s = candidate.indexOf('{');
    const e2 = candidate.lastIndexOf('}');
    let jsonStr = candidate.substring(s, e2 + 1);
    jsonStr = jsonStr.replace(/,\s*([\]\}])/g, '$1');
    result = JSON.parse(jsonStr);
  } catch (e3) {
    result = { document_title: 'Parse error', sections: [], _raw: raw.substring(0, 2000) };
  }
}

return [{ json: { documentPlan: result } }];
