var body = $json.body || $json;
var reqs = body.requirements || {};
var reqType = reqs.procurement_type || 'неопределен';
var reqStr = JSON.stringify(reqs, null, 2);
if (reqStr.length > 30000) reqStr = reqStr.substring(0, 30000);
var specStr = JSON.stringify(body.specData || {}, null, 2);
if (specStr.length > 30000) specStr = specStr.substring(0, 30000);
var ciStr = JSON.stringify(body.contractorInfo || {}, null, 2);

var p = 'Ти си експерт по структуриране на технически предложения за обществени поръчки.

Получаваш:

';
p += 'ТИП ПОРЪЧКА: ' + reqType + '

';
p += 'ИЗВЛЕЧЕНИ ИЗИСКВАНИЯ:
' + reqStr + '

';
p += 'АНАЛИЗ НА СПЕЦИФИКАЦИЯТА:
' + specStr + '

';
p += 'ИНФОРМАЦИЯ ЗА ИЗПЪЛНИТЕЛЯ:
' + ciStr + '

';
p += '## ЗАДАЧА
Създай ДЕТАЙЛЕН план на техническото предложение.

';
p += 'Всяка секция трябва да е ПОДРОБНО разписана защото ДРУГ агент ще пише всяка секция ОТДЕЛНО по твоя план. ';
p += 'Той ще вижда САМО плана за своята секция, затова указанията трябва да са максимално конкретни.

';
p += '## ПРАВИЛА ЗА ПЛАНА
';
p += '1. Една секция за ВСЯКО изискване от документацията
';
p += '2. Минимум 3-5 подсекции на секция с КОНКРЕТНИ указания
';
p += '3. За всяка подсекция: какво точно да се напише, какви данни от спецификацията да се ползват
';
p += '4. Планирай таблици: заглавие + колони (оборудване, персонал, графици, качество)
';
p += '5. Планирай графици като таблици: Дейност | Месец 1 | Месец 2 | ...
';
p += '6. Реалистичен обем: 30-100 стр. в зависимост от сложността
';
p += '7. НЕ добавяй секции за несъществуващи изисквания
';
p += '8. Ако requirements е празен → създай базова структура (представяне, подход, график, организация)
';
p += '9. Ако specData полета са null → не создавай секции за тях

';
p += '## ФОРМАТ
Върни САМО валиден JSON (БЕЗ markdown блокове):
';
p += '{
  "document_title": "ТЕХНИЧЕСКО ПРЕДЛОЖЕНИЕ за [точно наименование от документацията]",
';
p += '  "procurement_type": "строителство | услуги | доставки | проектиране",
';
p += '  "total_estimated_pages": 60,
';
p += '  "sections": [
    {
';
p += '      "id": "1",
      "title": "Заглавие от изискването",
';
p += '      "requirement_id": "1",
      "estimated_pages": 8,
';
p += '      "subsections": [
        {
';
p += '          "id": "1.1",
          "title": "Подзаглавие",
';
p += '          "requirement_id": "1.1",
          "estimated_pages": 2,
';
p += '          "content_guidance": ["Напишете конкретно X", "Опишете подход за Y", "Включете таблица за Z"],
';
p += '          "spec_data_to_use": ["object.name", "technical_parameters[0].value"],
';
p += '          "tables_needed": [{"title": "Списък оборудване", "columns": ["Тип", "Марка/модел", "Брой", "Характеристики"]}]
';
p += '        }
      ]
    }
  ],
';
p += '  "appendices": [{"title": "", "description": ""}]
}

';
p += 'САМО JSON, без друг текст.';

return [{ json: { prompt: p } }];

var raw = ($json.text || $json.response || '').trim();
var result;

try {
  result = JSON.parse(raw);
} catch (e) {
  var m = raw.match(/```(?:json)?\s*([\s\S]*?)```/);
  var candidate = m ? m[1].trim() : raw;
  try {
    var s = candidate.indexOf('{');
    var e2 = candidate.lastIndexOf('}');
    var jsonStr = candidate.substring(s, e2 + 1);
    jsonStr = jsonStr.replace(/,\s*([\]\}])/g, '$1');
    result = JSON.parse(jsonStr);
  } catch (e3) {
    result = { document_title: 'Parse error', sections: [], _raw: raw.substring(0, 2000) };
  }
}

return [{ json: { documentPlan: result } }];
