var raw = ($json.text || $json.response || '').trim();
var result;

try {
  result = JSON.parse(raw);
} catch (e) {
  var m = raw.match(/```(?:json)?\s*([\s\S]*?)```/);
  var candidate = m ? m[1].trim() : raw;
  try {
    var s = candidate.indexOf('{');
    var e2 = candidate.lastIndexOf('}');
    var js = candidate.substring(s, e2 + 1).replace(/,\s*([\]\}])/g, '$1');
    result = JSON.parse(js);
  } catch (e3) {
    result = { passed: true, score: 70, issues: ['Parse error — defaulting to PASS'], feedback: '' };
  }
}

result.passed = result.passed !== false && (result.score || 80) >= 80;

return [{ json: result }];
